import React, { Component } from "react";
import { Route } from "react-router-dom";
import { connect } from "react-redux";

import Dashboard from "./pages/dashboard";
import SplashScreen from "../components/splashScreen";
import ProjectPage from "./pages/projectPage";
import SettingsPage from "./pages/settingsPage";
import ProjectsPage from "./pages/projectsPage";
import NewProjectForm from "../components/forms/newProjectForm";
import ModalWindow from "./modalWindow";
import Toggle from "./toggle";
import PageHeader from "../components/pageHeader";
import PageSidebar from "../components/pageSidebar";

class PageContent extends Component {
  render() {
    return (
      <div>
        <div>
          <PageHeader />
        </div>
        <div className="page-main">
          {/* Sidebar */}
          <PageSidebar />
          {/* Content */}
          {/* <PageContent /> */}
          <div className="full-width">
            {/* <Route exact path="/" component={Dashboard} /> */}
            {/* TODO: if localstorage JWT present, redirect to dashbaord, if not, redirect to login */}
            {/* <Route exact path="/login" component={SplashScreen} /> */}
            <Route exact path="/dashboard" component={Dashboard} />
            <Route exact path="/projects" component={ProjectsPage} />
            <Route
              // exact
              path="/projects/:id"
              render={(routerProps) => (
                <ProjectPage
                  {...routerProps}
                  projects={this.props.projects}
                  activeProject={this.props.activeProject}
                />
              )}
            />
            <Route exact path="/settings" component={SettingsPage} />

            <Toggle id={"newProjectFormToggle"}>
              <ModalWindow component={<NewProjectForm />} />
            </Toggle>
          </div>
        </div>
      </div>
    );
  }

  componentDidMount() {
    this.props.loadProjects();
    this.props.loadBugs();
    // TODO: this needs to move to page content component
  }
}

const mapStateToProps = (state) => {
  return {
    projects: state.projects.allProjects,
    activeProject: state.projects.activeProject,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    loadProjects: () => dispatch(loadProjects()),
    loadBugs: () => dispatch(loadBugs()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(PageContent);
