import React, { Component } from "react";

class NewBugForm extends Component {
  render() {
    return (
      <div>
        This is the new bug form :)
        {/* TODO: fill in an create form content */}
        <form onSubmit={this.handleOnSubmit}>
          <label htmlFor="bug-id" className="col-sm-20">
            Bug ID:{" "}
          </label>
          <input type="text" name="bug-id" />
          <label htmlFor="bug-title">Title: </label>
          <input type="text" name="bug-title" /> <br />
          <label htmlFor="bug-description">Description: </label>
          <textarea name="bug-description"></textarea> <br />
          <label htmlFor="bug-tags">Tags: </label>
          <textarea name="bug-tags"></textarea> <br />
          <label htmlFor="bug-details">Details: </label>
          <textarea name="bug-details"></textarea> <br />
          <label htmlFor="bug-line-no">File/Line No: </label>
          <input type="text" name="bug-line-no" /> <br />
          <label htmlFor="bug-priority">Priority: </label>
          <select name="bug-priority">
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
            <option value="critical">Critical</option>
            <option value="roadblock">Roadblock</option>
          </select>
          <label htmlFor="bug-due-date">Due Date: </label>
          <input type="date" name="bug-due-date" />
        </form>
      </div>
    );
  }

  handleOnSubmit = (event) => {
    event.preventDefault();
    // TODO: submit form logic, store in state and post fetch to API
  };
}

export default NewBugForm;
