function bugs(
  state = {
    bugs: [],
  },
  action
) {
  // TODO: Build this reducer
  switch (action.type) {
    case "ADD_BUG":
      return { allBugs: [...state.allBugs, action.bug] };
    case "LOAD_BUGS":
      return { allBugs: state.bugs.concat(action.bugs) };
    case "EDIT_BUG":
      return state;
    case "DELETE_BUG":
      return state;
    case "SET_ACTIVE_BUG":
      console.log("inside set active bug reducer");
      console.log("action Bug:", action.bug);
      return { activeBug: action.bug };
    default:
      return state;
  }
}

export default bugs;
