import React, { Component } from "react";
import ProgressBar from "react-bootstrap/ProgressBar";
import { connect } from "react-redux";
import { generateProjectStatusBarData } from "../../helpers/projectHelpers";
// import Button from "react-bootstrap/Button";

class ProjectStatus extends Component {
  render() {
    return (
      <div>
        ---------------------------------------
        <br />
        | Future home of a sweet status bar :) |<br />
        ---------------------------------------
        {/* TODO: Fill in project status content */}
        <ProgressBar>
          <ProgressBar animated variant="warning" now={20} key={2} />
          <ProgressBar animated variant="danger" now={10} key={3} />
          <ProgressBar animated variant="success" now={35} key={1} />
        </ProgressBar>
      </div>
    );
  }

  calcProgressChartData() {
    const data = generateProjectStatusBarData(
      this.props.project,
      this.state.bugs
    );
    console.log(data);
  }
}

const mapStateToProps = (state) => {
  return {
    project: state.projects.activeProject,
    bugs: state.bugs.allBugs,
  };
};

export default connect(mapStateToProps)(ProjectStatus);
