import React, { Component } from "react";
import { connect } from "react-redux";
import { createUser } from "../../actions/authActions";

class NewUserForm extends Component {
  state = {
    user: {
      username: "",
      password: "",
      first_name: "",
      last_name: "",
    },
    password_confirmation: "",
    errors: "",
  };

  render() {
    return (
      <div>
        <div>{this.renderErrors()}</div>
        <form onSubmit={this.handleOnSubmit}>
          <label htmlFor="username">Username: </label>
          <input
            type="text"
            id="username"
            name="username"
            value={this.state.username}
            onChange={this.handleOnChange}
          />
          <label htmlFor="first_name">First Name: </label>
          <input
            type="text"
            id="first_name"
            name="first_name"
            value={this.state.first_name}
            onChange={this.handleOnChange}
          />
          <label htmlFor="last_name">Last Name: </label>
          <input
            type="text"
            id="last_name"
            name="last_name"
            value={this.state.last_name}
            onChange={this.handleOnChange}
          />
          <label htmlFor="password">Password: </label>
          <input
            type="password"
            id="password"
            name="password"
            value={this.state.password}
            onChange={this.handleOnChange}
          />
          {/* <label htmlFor="password_confirmation">Confirm Password: </label>
          <input
            type="password"
            id="password_confirmation"
            name="password_confirmation"
            value={this.state.password_confirmation}
            onChange={this.handleOnChange}
          /> */}
          {/* <p>{this.state.errors}</p> */}
          <input type="submit" value="Create User" />
        </form>
      </div>
    );
  }

  handleOnSubmit = (event) => {
    event.preventDefault();
    console.log("New user form Submitted!");
    if (this.validate()) {
      this.props.createUser({ user: this.state.user });
    }
  };

  validate() {
    let isValid = true;
    this.setState({
      errors: "",
    });
    // check fields TODO:
    // if (this.state.password !== this.state.password_confirmation) {
    //   this.setState({ errors: { password: "Passwords do not match." } });
    //   return false;
    // } else {
    //   this.setState({ errors: "" });
    //   return true;
    // }
    // Check username
    if (this.state.username === "") {
      isValid = false;
      this.setState({
        errors: [...this.state.errors, "Please enter a username"],
      });
    }
    // Check first name
    if (this.state.first_name === "") {
      isValid = false;
      this.setState({
        errors: [...this.state.errors, "Please enter your first name"],
      });
    }
    // Check last name
    if (this.state.last_name === "") {
      isValid = false;
      this.setState({
        errors: [...this.state.errors, "Please enter your last name"],
      });
    }
    // check password
    if (this.state.password === "") {
      isValid = false;
      this.setState({
        errors: [...this.state.errors, "Please enter a password"],
      });
    }
    // check password confirmation
    if (this.state.password_confirmation !== this.state.password) {
      isValid = false;
      this.setState({
        errors: [...this.state.errors, "Passwords do not match"],
      });
    }

    console.log(this.state.errors);
    return isValid;
  }

  handleOnChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  renderErrors = () => {
    let errorElements = null;
    if (this.state.errors !== "") {
      errorElements = this.state.errors.map((error, id) => {
        return (
          <h3 className="error_text" key={id}>
            {error}
          </h3>
        );
      });
    }

    return errorElements;
  };
}

const mapDispatchToProps = (dispatch) => {
  return {
    createUser: (userData) => dispatch(createUser(userData)),
  };
};

export default connect(null, mapDispatchToProps)(NewUserForm);
