import React, { Component } from "react";
import { Route } from "react-router-dom";
import { connect } from "react-redux";

import Dashboard from "./pages/dashboard";
import SplashScreen from "../components/splashScreen";
import ProjectPage from "./pages/projectPage";
import SettingsPage from "./pages/settingsPage";
import ProjectsPage from "./pages/projectsPage";
import NewProjectForm from "../components/forms/newProjectForm";
import NewBugForm from "../components/forms/newBugForm";
import ModalWindow from "./modalWindow";

import { Toggle } from "./toggle";
import { showToggle, hideToggle } from "../actions/toggleActions";

class PageContent extends Component {
  render() {
    return (
      <div className="full-width">
        <Route exact path="/" component={Dashboard} />
        <Route exact path="/login" component={SplashScreen} />
        <Route exact path="/dashboard" component={Dashboard} />
        <Route exact path="/projects" component={ProjectsPage} />
        <Route exact path="/projects/:id" component={ProjectPage} />
        <Route exact path="/settings" component={SettingsPage} />

        {/* <ModalWindow /> */}
        {/* <Route exact path="/newProject"> */}
        {/* <Toggle id={"newProjectFormToggle"}>
          <ModalWindow
            component={<NewProjectForm />}
            onClick={() => this.props.hideToggle("newProjectFormToggle")}
          />
        </Toggle> */}

        {/* </Route> */}
        {/* <Route exact path="/newBug"> */}
        {/* // {this.renderNewBugModal()} */}
        <Toggle id={"newBugFormToggle"}>
          <ModalWindow
            component={<NewBugForm />}
            onClick={this.props.hideToggle("newBugFormToggle")}
          />
        </Toggle>
        {/* </Route> */}
      </div>
    );
  }

  // FORM FUNCTIONS
  // renderNewBugModal() {
  //   // console.log(this.props);
  //   if (this.props.showNewBugModal) {
  //     <ModalWindow component={<NewBugForm />} />;
  //   }
  // }
}

// const mapStateToProps = (state) => {
//   console.log(state);
//   return {
//     showNewBugModal: state.showNewBugModal,
//   };
// };
const mapDispatchToProps = (dispatch) => {
  return {
    showToggle: (id) => dispatch(showToggle(id)),
    hideToggle: (id) => dispatch(hideToggle(id)),
  };
};

export default connect(null, mapDispatchToProps)(PageContent);
