function bugsReducer(state = { bugs: [] }, action) {
  // TODO: Build this reducer
  switch (action.type) {
    case "ADD_BUG":
      // console.log("Woo! Im in the ADD_BUG action!");
      // console.log({ bugs: [...state.bugs, action.bug] });
      return [...state.bugs, action.bug];
    case "EDIT_BUG":
      return state;
    case "DELETE_BUG":
      return state;
    default:
      return state;
  }
}

export default bugsReducer;
