// import { withRouter, browserHistory, useHistory } from "react-router-dom";
import { push } from "react-router-dom";
export const createBug = (bugObject, history) => {
  return (dispatch) => {
    // dispatch({ type: "ADD_BUG" }) TODO: add loading action;
    // const formData = bugObject
    // console.log(bugObject);
    const configurationObject = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify(bugObject),
    };
    fetch(`http://localhost:8000/bugs`, configurationObject) //TODO: get correct url for fetch and set up post
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        // console.log(json);
        // const history = useHistory();
        dispatch({ type: "ADD_BUG", bug: json });
        // return <Redirect to="/projects" />;
        history.push("/projects");
        // useRedirectToProjects();
        // return <Redirect to="/projects" />;
        push("/projects");
      })
      .catch((response) => {
        alert("Woops, something went wrong creating the bug");
        console.log(response);
      });
  };
};

