// import { showNewBugModal } from "../actions/formActions";

function formsReducer(
  state = {
    modals: {
      showNewBugModal: false,
      showEditBugModal: false,
      showNewProjectModal: false,
      showEditProjectModal: false,
    },
  },
  action
) {
  switch (action.type) {
    case "SHOW_NEW_BUG_MODAL":
      // const showModal = [...state];
      // console.log("showing the new bug modal action");
      console.log({ modals: { ...state.modals, showNewBugModal: true } });
      return { modals: { ...state.modals, state } };
    case "HIDE_NEW_BUG_MODAL":
      return { showNewBugModal: false };
    default:
      return state;
  }
}

export default formsReducer;
