class RandomIdGenerator {
  constructor() {
    this._characters = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ";
  }

  static createBase36(length) {
    let string = "";

    for (let i = 0; i < length; i++) {
      string +=
        this._characters[Math.floor(Math.random()) * this._characters.length];
    }

    return string;
  }
}

export default RandomIdGenerator;
