function toggleReducer(state = { toggles: {} }, action) {
  switch (action.type) {
    case "SHOW":
      console.log({ ...state, [action.payload]: true });
      return { toggles: { ...state.toggles, [action.payload]: true } };
    case "HIDE":
      console.log({ ...state, [action.payload]: false });
      return { toggles: { ...state.toggles, [action.payload]: false } };
    default:
      return state;
  }
}

export default toggleReducer;
