import React, { Component } from "react";

class PriorityTasks extends Component {
  render() {
    return (
      <div className="test-border" id="priority-tasks-panel">
        --This is the priority tasks component :)
        {/* TODO: fill in content */}
        <table>
          <thead>
            <tr>
              <th>Bug Id</th>
              <th>Title</th>
              <th>Priority</th>
              <th>Due Date</th>
              <th>Status</th>
              <th>Project</th>
            </tr>
          </thead>
          <tbody>{this.renderPriorityTableRows()}</tbody>
        </table>
      </div>
    );
  }

  renderPriorityTableRows() {
    if (this.props.bugs) {
      // console.log(this.props.bugs);
      // create array sorted by priority
      let criticalBugs = this.props.bugs.filter(
        (bug) => bug.bugPriority === "Critical"
      );

      // then sort within each priority by due date
      let elements = [];
      // let criticalBugs = this.props.bugs.map((bug, index) => {
      //   if (bug.bugPriority === "Critical") {
      //     return (
      //       <tr key={index}>
      //         <td>{bug.bugId}</td>
      //         <td>{bug.bugtitle}</td>
      //         <td>{bug.bugPriority}</td>
      //         <td>{bug.bugDueDate}</td>
      //         <td>{bug.bugStatus}</td>
      //         <td>{bug.project_id}</td>
      //       </tr>
      //     );
      //   }
      // });
      console.log(criticalBugs);
      criticalBugs.sort((firstBug, secondBug) => {
        // console.log(firstBug);
        // console.log(firstBug.bugDueDate);
        // console.log(new Date(firstBug.bugDueDate));
        return new Date(firstBug.bugDueDate) - new Date(secondBug.bugDueDate);
      });

      elements.push(criticalBugs);
      // return elements;
    }
  }
}

export default PriorityTasks;
