function auth(
  state = {
    auth: [],
  },
  action
) {
  switch (action.type) {
    case "SET_USER":
      console.log("Action: ", action.user);
      return { current_user: action.user };
    case "LOGOUT":
      return state;
    default:
      return state;
  }
}

export default auth;
